mapper.update:
update exam_template_info
set exam_template_id = #{examTemplateId,jdbcType=INTEGER},
  non_drug_item_id = #{nonDrugItemId,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, exam_template_id, non_drug_item_id
from exam_template_info
where id = #{id,jdbcType=INTEGER}
